(function($) {
    "use strict";

    $('.validate-form').on('submit', function() {
        var input = $('.validate-input .input100');
        var check = true;

        for (var i = 0; i < input.length; i++) {
            if (validate(input[i]) == false) {
                showValidate(input[i]);
                check = false;
            }
        }

        return check;
    });


    $('.validate-form .input100').each(function() {
        $(this).focus(function() {
            hideValidate(this);
        });
    });

    $('.validate-form1').on('submit', function() {
        var input = $('.validate-input1 .input1001');
        var check = true;

        for (var i = 0; i < input.length; i++) {
            if (validate(input[i]) == false) {
                showValidate(input[i]);
                check = false;
            }
        }

        return check;
    });


    $('.validate-form1 .input1001').each(function() {
        $(this).focus(function() {
            hideValidate(this);
        });
    });



    function validate(input) {
        if ($(input).attr('type') == 'email' || $(input).attr('name') == 'email') {
            if ($(input).val().trim().match(/^([a-zA-Z0-9_\-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([a-zA-Z0-9\-]+\.)+))([a-zA-Z]{1,5}|[0-9]{1,3})(\]?)$/) == null) {
                return false;
            }
        } else if ($(input).attr('name') == 'nameTask') {
            if ($(input).val().trim().length < 5 || $(input).val().trim().length > 200) {
                return false;
            }
        } else if ($(input).attr('name') == 'dates') {
            const str = $(input).val().trim();
            let now = new Date();
            try {
                const date = str.split(" ");
                const date2 = date[0].split(".");
                const l = date2[2] + "-" + date2[1] + "-" + date2[0];
                let l2 = date[1].split(":");
                now = new Date(l);
                now.setUTCHours(l2[0] - 3);
                now.setUTCMinutes(l2[1]);
            } catch (e) {}
            if ($(input).val().trim().match(/^(0?[1-9]|[12][0-9]|3[01]).(0?[1-9]|1[012]).\d{4} ([01]\d|2[0-3]):?([0-5]\d)$/) == null || now < new Date()) {
                return false;
            }
        } else {
            if ($(input).val().trim() == '') {
                return false;
            }
        }
    }

    function showValidate(input) {
        var thisAlert = $(input).parent();

        $(thisAlert).addClass('alert-validate');
    }

    function hideValidate(input) {
        var thisAlert = $(input).parent();

        $(thisAlert).removeClass('alert-validate');
    }
})(jQuery);

